import React from 'react';
import s from './CalcDispaly.module.scss'
import { CalculatorType, ItemTypes, ModeType } from '../Calculator';

type CalcDisplayType = {
    board: ModeType
    draggable: boolean
    currentBoard: null | ModeType
    item: ItemTypes
    currentItem: null | ItemTypes
    boards: CalculatorType
    setCurrentBoard: (board: null | ModeType) => void
    setCurrentItem: (item: null | ItemTypes) => void
    setBoards: (calc_data: CalculatorType) => void
}



export const CalcDisplay = ({ board, draggable,
    item, setCurrentBoard, setCurrentItem, currentBoard, currentItem, setBoards, boards }: CalcDisplayType) => {



    const dragOverHandler = (e: React.DragEvent<HTMLDivElement>): void => {
        e.preventDefault()
        if (e.currentTarget.className === s.disp_wrapper) {
            e.currentTarget.style.boxShadow = " 0px 4px 6px rgba(0, 0, 0, 0.06), 0px 6px 8px rgba(0, 0, 0, 0.1)"
        }

    }

    const dragLeaveHandler = (e: React.DragEvent<HTMLDivElement>): void => {
        e.currentTarget.style.boxShadow = " 0px 2px 4px rgba(0, 0, 0, 0.06), 0px 4px 6px rgba(0, 0, 0, 0.1)"
    }
    const dragStartHandler = (e: React.DragEvent<HTMLDivElement>,
        board: ModeType, item: ItemTypes): void => {
        setCurrentBoard(board)
        setCurrentItem(item)
    }
    const dragEndHandler = (e: React.DragEvent<HTMLDivElement>): void => {
        e.currentTarget.style.boxShadow = " 0px 2px 4px rgba(0, 0, 0, 0.06), 0px 4px 6px rgba(0, 0, 0, 0.1)"

    }
    const onDropHandler = (e: React.DragEvent<HTMLDivElement>,
        board: ModeType, item: null | ItemTypes): void => {
        e.preventDefault()
        const currentIndex = currentBoard?.calc_items.indexOf(currentItem)
        currentBoard?.calc_items.splice(currentIndex!, 1)
        const dropIndex = board.calc_items.indexOf(item)
        board.calc_items.splice(dropIndex + 1, 0, currentItem)
        setBoards(boards.map(b => {
            if (b.id === board.id) {
                return board
            }
            if (b.id === currentBoard!.id) {
                return currentBoard!
            }
            return b
        }))
        console.log(currentBoard?.calc_items)
        console.log(currentItem)
        console.log(currentIndex)
    }


    return (

        <div className={s.disp_wrapper}
            onDragOver={(e) => dragOverHandler(e)}
            onDragLeave={(e) => dragLeaveHandler(e)}
            onDragStart={(e) => dragStartHandler(e, board, item)}
            onDragEnd={(e) => dragEndHandler(e)}
            onDrop={(e) => onDropHandler(e, board, item)}
            draggable={true}>
            <div className={s.calc_display}>

            </div>
        </div>
    );


}
