import { PayloadAction, createAsyncThunk, createSlice } from "@reduxjs/toolkit"

type button = {
    id: number
    val: string
}
export type initialStateType = {
    display_value: string
    buttons: Array<button>
    operators: Array<button>
}

const initialState: initialStateType = {
    display_value: '0',
    buttons: [
        { id: 1, val: '1' },
        { id: 2, val: '2' },
        { id: 3, val: '3' },
        { id: 4, val: '4' },
        { id: 5, val: '5' },
        { id: 6, val: '6' },
        { id: 7, val: '7' },
        { id: 8, val: '8' },
        { id: 9, val: '9' },
        { id: 0, val: '0' },
        { id: 10, val: ',' }
    ],
    operators: [
        { id: 11, val: '/' },
        { id: 12, val: '*' },
        { id: 13, val: '+' },
        { id: 14, val: '-' },
        { id: 15, val: '=' }
    ]

}

export const CalcThunk = createAsyncThunk('calculator/CalcThunk', (val: string, thunkAPI) => {
    //thunkAPI.dispatch(setStatusApp({ status: 'loading' }))
    try {

        thunkAPI.dispatch(displayShow({ val }))
        // thunkAPI.dispatch(setStatusApp({ status: 'succeeded' }))
    }
    catch (e) {
        // const err = e as Error | AxiosError
        // handleError(err, thunkAPI.dispatch)
        // thunkAPI.dispatch(setStatusApp({ status: 'failed' }))
    }
})

const slice = createSlice({
    name: 'calculator',
    initialState,
    reducers: {
        displayShow(state, action: PayloadAction<{ val: string }>) {
            state.display_value = state.display_value === '0' ? action.payload.val : state.display_value + action.payload.val
        },
        math_count(state, action: PayloadAction<{}>) {

        }
    }
})

export const CalculatorReducer = slice.reducer
export const { math_count, displayShow } = slice.actions

export type CalcActionsType = MathCountType | DisplayShow
export type MathCountType = ReturnType<typeof math_count>
export type DisplayShow = ReturnType<typeof displayShow>
