import React from 'react';
import s from './CalcDispaly.module.scss'
import { ItemTypes, ModeType } from '../Calculator';
import { useAppDispatch, useAppSelector } from '../../hooks/HooksForState';
import { useKeyPress } from '../../hooks/KeyUpDownHook';
import { CalcThunk } from '../../state_manager/reducers/calc_reducer';

type CalcDisplayType = {
    board: ModeType
    draggable: boolean
    item: ItemTypes
    setCurrentBoard: (board: null | ModeType) => void
    setCurrentItem: (item: null | ItemTypes) => void

}



export const CalcDisplay = ({ board, draggable,
    item, setCurrentBoard, setCurrentItem }: CalcDisplayType) => {

    const display_value = useAppSelector(store => store.calc.display_value)
    const dispatch = useAppDispatch()
    const one = useKeyPress('1')
    const two = useKeyPress('2')
    const tree = useKeyPress('3')
    const four = useKeyPress('4')
    const five = useKeyPress('5')
    const six = useKeyPress('6')
    const seven = useKeyPress('7')
    const eight = useKeyPress('8')
    const nine = useKeyPress('9')
    const zero = useKeyPress('0')
    const dot = useKeyPress(',')

    if (one) dispatch(CalcThunk('1'))
    if (two) dispatch(CalcThunk('2'))
    if (tree) dispatch(CalcThunk('3'))
    if (four) dispatch(CalcThunk('4'))
    if (five) dispatch(CalcThunk('5'))
    if (six) dispatch(CalcThunk('6'))
    if (seven) dispatch(CalcThunk('7'))
    if (eight) dispatch(CalcThunk('8'))
    if (nine) dispatch(CalcThunk('9'))
    if (zero) dispatch(CalcThunk('0'))
    if (dot) dispatch(CalcThunk(','))

    const dragStartHandler = (e: React.DragEvent<HTMLDivElement>,
        board: ModeType, item: ItemTypes): void => {
        setCurrentBoard(board)
        setCurrentItem(item)
    }


    return (

        <div className={s.disp_wrapper}
            onDragStart={draggable ? (e) => dragStartHandler(e, board, item) : () => { }}
            draggable={draggable}>
            <div className={s.calc_display}>
                {display_value}
            </div>
        </div>
    );


}
