import React from 'react';
import s from './CalcDispaly.module.scss'
import { ButtonNumberType, DisplayType, EqualType, ModeType, OperationsType } from '../Calculator';

type CalcDisplayType = {
    board: ModeType
    draggable: boolean
    item: DisplayType | ButtonNumberType | OperationsType | EqualType
}

const shadowStyle = {
    boxShadow: " 0px 2px 4px rgba(0, 0, 0, 0.06), 0px 4px 6px rgba(0, 0, 0, 0.1)"
}
console.log(shadowStyle);

export const CalcDisplay = ({ board, draggable, item }: CalcDisplayType) => {



    const dragOverHandler = (e: React.DragEvent<HTMLDivElement>,
        board: ModeType, item: DisplayType | ButtonNumberType | OperationsType | EqualType): void => {
        e.preventDefault()
        if (e.currentTarget.className === s.disp_wrapper) {
            e.currentTarget.style.boxShadow = '0 2px 3px gray'
        }

    }

    const dragLeaveHandler = (e: React.DragEvent<HTMLDivElement>): void => {
        e.currentTarget.style.boxShadow = 'none'

    }
    const dragStartHandler = (e: React.DragEvent<HTMLDivElement>): void => {

        console.log(e)
    }
    const dragEndHandler = (e: React.DragEvent<HTMLDivElement>): void => {
        e.currentTarget.style.boxShadow = 'none'

    }
    const onDropHandler = (e: React.DragEvent<HTMLDivElement>,
        board: ModeType, item: DisplayType | ButtonNumberType | OperationsType | EqualType): void => {
        e.preventDefault()
        console.log(e)
    }


    return (

        <div className={s.disp_wrapper}
            onDragOver={(e) => dragOverHandler(e, board, item)}
            onDragLeave={(e) => dragLeaveHandler(e)}
            onDragStart={(e) => dragStartHandler(e)}
            onDragEnd={(e) => dragEndHandler(e)}
            onDrop={(e) => onDropHandler(e, board, item)}
            draggable={true}>
            <div className={s.calc_display}>

            </div>
        </div>
    );


}
