import { PayloadAction, createAsyncThunk, createSlice } from "@reduxjs/toolkit"
import { setErrorApp } from "./app_reducer"
import { AppRootStateType } from "../store"

type button = {
    id: number
    val: string
}
export type initialStateType = {
    display_value: string
    buttons: Array<button>
    operators: Array<button>
}

const initialState: initialStateType = {
    display_value: '0',
    buttons: [
        { id: 1, val: '1' },
        { id: 2, val: '2' },
        { id: 3, val: '3' },
        { id: 4, val: '4' },
        { id: 5, val: '5' },
        { id: 6, val: '6' },
        { id: 7, val: '7' },
        { id: 8, val: '8' },
        { id: 9, val: '9' },
        { id: 0, val: '0' },
        { id: 10, val: ',' }
    ],
    operators: [
        { id: 11, val: '/' },
        { id: 12, val: '*' },
        { id: 13, val: '+' },
        { id: 14, val: '-' },
        { id: 15, val: '=' }
    ]

}

export const CalcThunk = createAsyncThunk('calculator/CalcThunk', (val: string, { getState, dispatch }) => {
    try {
        const { calc } = getState() as AppRootStateType

        calc.display_value.length > 9 ? dispatch(setErrorApp) : dispatch(displayShow({ val }))


    }
    catch (e) {
        // const err = e as Error | AxiosError
        // handleError(err, thunkAPI.dispatch)
        // thunkAPI.dispatch(setStatusApp({ status: 'failed' }))
    }
})
